export type BaseItem = {
    id: string;
    [key: string]: any;
};

export type Update<T extends BaseItem> = {
    type: "update";
    timestamp: number;
    id: string;
    value: T;
    metaValue?: undefined;
    overlap?: number;
};

export type Delete<T extends BaseItem> = {
    type: "delete";
    timestamp: number;
    id: string;
    value: T["id"];
    metaValue?: undefined;
    overlap?: number;
};

export type MetaUpdate = {
    type: "meta";
    timestamp: number;
    id: string;
    value?: undefined;
    metaValue: any;
    overlap?: number;
};

export type FullAction<T extends BaseItem> = Update<T> | Delete<T> | MetaUpdate;

export type Action<T extends BaseItem> = Omit<
    FullAction<T>,
    "timestamp" | "id"
> & {
    timestamp?: number;
};

export type Full<T extends BaseItem> = T & {
    __delete_at?: number;
    __create_at: number;
    __update_at: number;
};
